mapper.select:
select 

from course
where cid = #{cid,jdbcType=INTEGER}
  and tid = #{tid,jdbcType=INTEGER}

mapper.delete:
delete from course
where cid = #{cid,jdbcType=INTEGER}
  and tid = #{tid,jdbcType=INTEGER}

mapper.update:
update course


    name = #{name,jdbcType=VARCHAR},


    tname = #{tname,jdbcType=VARCHAR},


where cid = #{cid,jdbcType=INTEGER}
  and tid = #{tid,jdbcType=INTEGER}

update course
set name = #{name,jdbcType=VARCHAR},
  tname = #{tname,jdbcType=VARCHAR}
where cid = #{cid,jdbcType=INTEGER}
  and tid = #{tid,jdbcType=INTEGER}
